import { useContext } from "react";
import { useState } from "react";
import { useEffect } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

export default function EditProfilePopup (props){
    const currentUser = useContext(CurrentUserContext);

    const [profileData, setProfileData] = useState({ name: '', about: '' })

    const handleNameChange = (e) => {
        setProfileData({
            name: e.target.value,
            about: profileData.about
        })
    }

    const handleDescriptionChange = (e) => {
        setProfileData({
            name: profileData.name,
            about: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        props.onSubmit(profileData)
    }

    // useEffect(() => {
    //     //Seting the Form Elements Content
    //     // setName(currentUser.name);
    //     // setDescription(currentUser.about);

    // }, [currentUser]);



    return (
        <PopupWithForm isOpen={props.isOpen} name="profile-edit" title="Редактировать Профиль" onClose={props.onClose} onSubmit={handleSubmit}>
            <input type="text" id="name-input" name="name" value={profileData.Name} onChange={handleNameChange} className="popup__input popup__input_type_name" placeholder="Введите имя" minLength="2" maxLength="40" required />
            <span className="popup__job-input-error"></span>
            <input type="text" id="job-input" name="job" value={profileData.about} onChange={handleDescriptionChange} className="popup__input popup__input_type_job" placeholder="Введите профессию" minLength="2" maxLength="200" required />
            <span className="popup__name-input-error"></span>
        </PopupWithForm>
    )
}
