import { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import register from "../auth";

export default function AuthForm(props){
    const history = useHistory();
    console.log(history);
    // const [userData, setUserData] = useState({emmail: '', password:''});
    console.log(props.userData)
    const handleChange = (e) => {
        console.log(props.userData);
        if(e.target.name === 'email'){
            props.onSetUserData({
                [e.target.name]: e.target.value,
                password: props.userData.password
            })
        } else {
            props.onSetUserData({
                email: props.userData.email,
                [e.target.name]: e.target.value
            })
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if(props.isRegistration){
            console.log(props.userData.email, props.userData.password);
            register(props.userData.email, props.userData.password)
            .then(res => console.log(res));
        }
    }

    return (
        <div className="authentification">
            <form className="authentification__form" onSubmit={handleSubmit}>
                <fieldset className="authentification__input-container">
                    <legend className="authentification__legend">{props.legend}</legend>
                    <input name='email' value={props.userData.email} onChange={handleChange} className="authentification__email" type="email" placeholder="Email" />
                    <input name='password' value={props.userData.password} onChange={handleChange} className="authentification__password" type="password" placeholder="Пароль" />
                    <button className="authentification__button" type="submit">{props.buttonText}</button>
                </fieldset>
                {props.isRegistration && <Link to="/sign-in" className="authentification__text">Уже зарегистрированы? Войти</Link>}
            </form>
        </div>
    )
}
