import { useState } from "react";
import { Link } from "react-router-dom";

export default function AuthForm(props){
    const [userData, setUserData] = useState({emmail: '', password:''});

    const handleChange = (e) => {
        console.log(e.target.value);
        setUserData({
            [e.target.name]: e.target.value
        })
    }
    return (
        <div className="authentification">
            <form className="authentification__form">
                <fieldset className="authentification__input-container">
                    <legend className="authentification__legend">{props.legend}</legend>
                    <input name='email' value={userData.name} onChange={handleChange} className="authentification__email" type="email" placeholder="Email" />
                    <input name='password' className="authentification__password" type="password" placeholder="Пароль" />
                    <button className="authentification__button" type="submit">{props.buttonText}</button>
                </fieldset>
                {props.isRegistration && <Link to="/sign-in" className="authentification__text">Уже зарегистрированы? Войти</Link>}
            </form>
        </div>
    )
}
