export const BASE_URL = 'https://auth.nomoreparties.co';

export function auth(email, password, endPoint){
    return fetch(`${BASE_URL}/${endPoint}`, {
        method: 'POST',
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            "email": email,
            "password": password
        })
    })
    .then(res => res.json())
    .then(res => res)
    .catch(e => console.log(e))

}

