import { Link } from "react-router-dom";

export default function AuthForm(props){
    return (
        <div className="authentification">
            <form className="authentification__form">
                <fieldset className="authentification__input-container">
                    <legend className="authentification__legend">{props.legend}</legend>
                    <input className="authentification__email" type="email" placeholder="Email" />
                    <input className="authentification__password" type="password" placeholder="Пароль" />
                    <button className="authentification__button" type="submit">Войти</button>
                </fieldset>
                {props.isRegistration && <Link to="/sign-up" className="authentification__text">Уже зарегистрированы? Войти</Link>}
            </form>
        </div>
    )
}
