import Header from './Header.jsx';
import Main from './Main.jsx';
import Footer from './Footer.jsx';
import PopupWithForm from './PopupWithForm.jsx';
import ImagePopup from './ImagePopup.jsx';
import { useState } from 'react';

function App() {

    // const [popupState, setPopupState] = useState({ isOpen: false, type: ''})
    const [selectedCard, setSelectedCard] = useState({isOpen: false, src: '', title: ''});

    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState( false );
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState ( false );
    const [isEditAvaterPopupOpen, setIsEditAvaterPopupOpen] = useState( false );

    const handleEditAvatarClick = () => {
        setIsEditAvaterPopupOpen( true );
    }

    const handleEditProfileClick = () => {
        setIsEditProfilePopupOpen( true );
    }

    const handleAddPlaceClick = () => {
        setIsAddPlacePopupOpen( true );
    }

    const handleCardClick = (e) => {
        setSelectedCard({isOpen: true, src: e.currentTarget.getAttribute('src'), title: e.currentTarget.getAttribute('alt') });
    }

    // const handlePopupOpen = (e) => {
    //     setPopupState({ isOpen: true, type: e.currentTarget.id})
    // }

    const closeAllPopups = (e) => {
        setIsEditAvaterPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);

        setSelectedCard({isOpen: false, src: '', alt: ''})
    }

    const checkKeyDown = (e) => {
        if(e.key === 'Escape'){
            closeAllPopups();
        }
    }

    document.addEventListener('keydown', checkKeyDown);

    return (
        <div className="page">
            <Header />
            <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvater={handleEditAvatarClick} onCardClick={handleCardClick}/>
            <Footer />

            <PopupWithForm name={'profile-edit'} title={'Редактировать профиль'} isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
                <input type="text" id="name-input" name="name" className="popup__input popup__input_type_name" placeholder="Введите имя" value="Жак-Ив Кусто" minlength="2" maxlength="40" required />
                <span className="popup__job-input-error"></span>
                <input type="text" id="job-input" name="job" className="popup__input popup__input_type_job" placeholder="Введите профессию" value="Иследователь" minlength="2" maxlength="200" required />
                <span className="popup__name-input-error"></span>
            </PopupWithForm>

            <PopupWithForm name={'edit-avatar'} title={'Обновить аватар'} isOpen={isEditAvaterPopupOpen} onClose={closeAllPopups}>
                <input type="url" id="avatar-url-input" name="link" className="popup__input popup__input_type_avatar-url" placeholder="Ссылка на картинку" value="" required />
                <span className="popup__error-avatar popup__avatar-url-input-error" id="avatar-url-error"></span>
            </PopupWithForm>

            <PopupWithForm name={'new-place'} title={'Новое место'} isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
                <input type="text" id="place-name" name="name" className="popup__input popup__input_type_place-name" placeholder="Название" value="" minlength="2" maxlength="30" required />
                <span className="popup__url-input-error"></span>
                <input type="url" id="url-input" name="link" className="popup__input popup__input_type_image-url" placeholder="Ссылка на картинку" value="" required />
                <span className="popup__place-name-error"></span>
            </PopupWithForm>

            <ImagePopup card={selectedCard} onClose={closeAllPopups} />     
        </div>
  )};

export default App;
