import { Link, useLocation } from "react-router-dom";
import headerLogo from "../images/headerLogo.svg";




function Header() {
  const location = useLocation();

  function generateContent(location){
    switch(location.pathname) {
      case '/login':
        console.log('login')
        return (
          <Link className="header__link header__auth-link" to="/sign-in">Регистрация</Link>
        )
      case '/sign-up':
        return (
          <Link className="header__link header__auth-link" to="/login">Войти</Link>
        )
      default:
        return (
          <>
          <p className="header__link header__username">user@user.ru</p>
          <Link className="header__link header__logout" to="/logout">Выйти</Link>
          </>
        )
    }
  }

  return (
    <header className="header">
      <img src={headerLogo} className="header__logo" alt="логотип" />
      <nav className="header__auth-menu">
        {
          generateContent(location)
        }  
      </nav>
    </header>
  );
}

export default Header;
