    })
    .then(res => console.log(res))
    .catch(e => console.log(e))

}

export function getContent(jwt){
    return fetch(`${BASE_URL}/users/me`, {
        method: 'GET',
        headers: {
            "Content-Type": "application/json",
            "Authorization" : `Bearer ${jwt}`
        }
    })
    .then(res => res.json())
    .then(res => res.data)
    .catch(e => console.log(e))
}
